/* ==================================================
 * @param	string  $user_agent
 * @return	object  $this
 * References: http://developer.emnet.ne.jp/useragent.html
 */
public function __construct($user_agent) {
	parent::__construct($user_agent);
	$this->theme      = ks_option('ks_theme_emobile');
	$this->operator   = 'EMOBILE';
	$this->type       = 'EMnet';
	$this->flat_rate  = true;
	$this->use_redir  = false;
	$this->show_plugin_icon = false;
	$this->user_agent = $user_agent;
	if (preg_match('!^emobile/\d+(\.\d+)+ *\((\w+);?!', $user_agent, $specs)) {
		$this->term_name  = $specs[2];
	} elseif (preg_match('!^Huawei/\d+(\.\d+)+/(\w+)/?!', $user_agent, $specs)) {
		$this->term_name  = $specs[2];
	}
	$this->sub_ID     = isset($_SERVER['HTTP_X_EM_UID']) ? $_SERVER['HTTP_X_EM_UID'] : NULL;
	$this->sub_ID_available = true;
	$this->page_size  = 20000;
	$this->cache_size = 1024000;
	$this->charset    = 'SJIS-win';
	$this->mime_type  = 'text/html';
	$this->preamble   = '<?xml version="1.0" encoding="__CHARSET__"?>
<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML Basic 1.0//EN"
"http://www.w3.org/TR/xhtml-basic/xhtml-basic10.dtd">';
	// use default xhtml_head
	$this->require_id_msg =   __("ERROR: Please allow transmission of user ID.\nGo EMnet Service &gt; Charges &amp; Application &gt; Contract Change &gt; Content Service &gt; User ID Transmission.", 'ktai_style');
	return;
}

