class Ktai_Service_Softbank_PDC extends Ktai_Service_Softbank {

/* ==================================================
 * @param	string  $user_agent
 * @return	object  $this
 */
public function __construct($user_agent) {
	parent::__construct($user_agent);
	$this->theme     = ks_option('ks_theme_sb_pdc');
	$this->type      = 'PDC';
	$this->flat_rate = false;
	preg_match('!^J-(PHONE|EMULATOR)/(\d\.\d+)/([-_.a-zA-Z0-9]+)/?(SN([-_.a-zA-Z0-9]*))? ?!', $user_agent, $specs);
	$this->term_name = isset($_SERVER['HTTP_X_JPHONE_MSNAME']) ? $_SERVER['HTTP_X_JPHONE_MSNAME'] : NULL;
	$this->term_name = $this->term_name ? $this->term_name : $specs[3];
	$this->term_ID   = isset($specs[5]) ? $specs[5] : NULL;
	$this->sub_ID    = isset($_SERVER['HTTP_X_JPHONE_UID']) ? $_SERVER['HTTP_X_JPHONE_UID'] : NULL;
	$this->page_size = 6000;
	if ($specs[2] < 4.0) {
		$this->cache_size = 6000;
		$this->require_id_msg = __("ERROR: Can't post a comment. We request your terminal's manufacture number but your phone does not available to trasmit it.", 'ktai_style');
	} else {
		$this->cache_size = 12000;
		$this->require_id_msg = __("ERROR: Please allow transmission of user infomation or your terminal's manifacture number.\nGo Yahoo! Japan &gt; My Softbank (User Support) &gt; Misc Application &gt; Transmission of User Infomation / Transmission of Manifacture Number.", 'ktai_style');
	}
	$this->preamble   = '';
	$this->xhtml_head = '';
	return;
}

/* ==================================================
 * @param	string  $buffer
 * @return	string  $buffer
 */
public function shrink_pre_split($buffer) {
	$buffer = preg_replace('!<h([1-6])([^>]*)>(.*?)</h\\1>!', '<div$2><p>$3</p></div>', $buffer);
	$buffer = preg_replace('#<img src="([^>]*?)\.gif"#', '<img src="\\1.png"', $buffer);
	$buffer = preg_replace('#<a href="([^>]*?)\.gif"#', '<a href="\\1.png"', $buffer);
	return parent::shrink_pre_split($buffer);
}

/* ==================================================
 * @param	string  $buffer
 * @return	string  $buffer
 */
public function shrink_post_split($buffer) {
	$buffer = str_replace('&nbsp;', ' ', $buffer);
	$buffer = preg_replace('!\s*</(li|dt|dd|option)>\s*!', '', $buffer);
	$buffer = preg_replace('! ?/ ?>!', '>', $buffer);
	$buffer = preg_replace('!<([-~_= a-zA-Z0-9%.,:;\'"#*/+]+?) ?/>!', '<$1>', $buffer); //"
	return parent::shrink_post_split($buffer);
}

// ===== End of class ====================
}

